class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0; //记录前面有几个1
        int ans = 0;
        int flag = 1;
        int k = s.length();
        for(int i = 0 ;i<k;i++)
        {
            if(s[i] == '0' && flag == 0)
            {
                ans+=cnt;
                flag = 1;
            }
            else if(s[i] == '1') {cnt++; flag = 0;}
        }
        return ans;

    }
};
