class Solution {
    public int maxOperations(String S) {
         int n = S.length();
        int ans = 0;
        char[] s = S.toCharArray();
        int cnt = 0;
        int l = 0, r = 0;
        while (r < n) {
            while (r < n && s[r] == '0') r++;
            l = r;
            while (r < n && s[r] == '1') r++;
             if (r == n) break;
            cnt += r - l;
            ans += cnt;
        }
        return ans;
    }
}
