        // 0346, last index = 6
        // 0->3, 3 no, 4->5
        // 0 no, 3->4
        // 0->3

        int result{};
        int oneCount{};
        bool seenZero{};

        for(int i{}; i < s.size(); ++i)
        {
            if(s[i] == '1')
            {
                if(seenZero)
                    result += oneCount;
                ++oneCount;
                seenZero = false;
            }
            else
                seenZero = true;
        }

        if(s[s.size()-1] == '0')
            result += oneCount;

        return result;
    }
