class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        o = 0
        res = 0
        for i in range(len(s)):

           if s[i] == '0':
               if i + 1 < len(s) and s[i + 1] == '1' or i + 1 == len(s):
                  res += o
           if s[i] == '1':
              o += 1
        return res
