class Solution {
public:
    int maxOperations(string s) {

        int val = 0;
        int p = 0;
        bool has_one = false;
        vector<int> b;

        for (char c : s) {
            if (c == '1') {
                b.push_back(1);
                has_one = true;
            } else if (c == '0' && has_one) {
                b.push_back(0);
                has_one = false;
            }
        }

        for (int bt : b) {
            if (bt == 1) {
                p += 1;
            } else {
                val += p;
            }
        }

        return val;

    }
};
