class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int i = 0, cnt = 0, ans = 0; 
        while(i < n){
            while(i < n && s[i]=='1') i++, cnt++;
            if(i < n) ans += cnt;
            while(i < n && s[i]=='0') i++;
        }
        return ans;
    }
};
