class Solution {
public:
    int maxOperations(string s) {
       int ans = 0;
    int ones_Count = 0;
    bool PreviousOne = false;
    vector<int> bits;

    for (char bit : s) {
        if (bit == '1') {
            bits.push_back(1);
            PreviousOne = true;
        } else if (bit == '0' && PreviousOne) {
            bits.push_back(0);
            PreviousOne = false;
        }
    }

    for (int bit : bits) {
        if (bit == 1) {
            ones_Count++;
        } else {
            ans += ones_Count;
        }
    }

    return ans;
    }
};
