class Solution {
public:
    int maxOperations(string s) {
        int x=0; int n=s.length();
        int ans=0;
        for( int i=n-1;i>0;i-- ){
            if(s[i]=='1')
             ans+=x;
          if(s[i]=='0'&&s[i-1]=='1'){
            x++;
          }

        }
        if(s[0]=='1'){
            ans+=x;
        }
        return ans;
    }
};
