class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        s_left = 0
        cur_idx = 0
        ans = 0
        while cur_idx < n:
            if s[cur_idx] == '0':
                while cur_idx < n and s[cur_idx] == '0':
                    cur_idx += 1
                ans += s_left
            else:
                s_left += 1
                cur_idx += 1
        return ans
