class Solution {
    public int maxOperations(String s) {
        byte[] sc = s.getBytes();
        int zCnt = (sc[sc.length - 1] == '0') ? 1 : 0;
        int moves = 0;
        for (int i = sc.length - 2; i >= 0; i--) {
            if (sc[i] == '0') {
                if (sc[i + 1] != '0') zCnt++;
            } else {
                moves += zCnt;
            }
        }
        return moves;
    }
}
