class Solution {
public:
    int maxOperations(string s) {
        int one_cnt = 0;
        int cnt = 0;
        for (int i = 0; i < s.length()-1; i++) {
            if (s[i] == '1') {
                one_cnt++;
            }

            if (s[i] == '1' && s[i+1] == '0') {
                cnt += one_cnt;
            } 
        }

        return cnt;
    }
};
