class Solution {
public:
    int maxOperations(string s) {

        int n = s.length();
        int count = 0;
        vector<int> as;
        for(int i=0; i<n; i++){
            if(s[i]=='1') count++;
            else{
                if(count>0) as.push_back(count);
                count = 0;
            }
        }

        int ans = 0, sum = 0;
        for(int i: as){
            sum += i;
            ans += sum;
        }

        return ans;

    }
};
