class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int op = 0;
        int front_found = 0;
        for(int i = n-2; i >= 0 ; i--) {
            if(s[i] == '1' && s[i+1] == '0') {
                front_found++;
                op += front_found;
                while(i >= 1) {
                    if(s[i-1] == '1') op += front_found;
                    else break;
                    i--;
                }
            }
        }
        return op;
    }
};
