class Solution {
public:
    int maxOperations(string s) {
        // 1100110101100011101011001110011
        int ans = 0;
        int prev_1 = 0;
        for(int i = 0; i < s.length(); i++){
            if(s[i] == '1'){
                prev_1++;
            } else {
                if(i != 0 && s[i-1] == '1'){
                    ans += prev_1;
                }
            }
        }
        return ans;
    }
};
