class Solution:
    def maxOperations(self, s: str) -> int:
        c = 0

        s = list(s)
        while s and s[-1] == "1":
            s.pop()

        n = len(s)

        c = 0
        ans = 0

        for i in range(n - 1):
            if s[i] == '1':
                c += 1

            elif s[i + 1] == '1':
                ans += c
                # c = 0

            # print(i, c, ans)
        return ans + c
