class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int v=0;
        bool flag=false;
        for(auto c:s){
            if(c=='0'&&flag)ans+=v,flag=false;
            else if(c=='1')v++,flag=true;
        }
        return ans;
    }
};
