class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        string t;
        for(int i=0;i<s.size();i++){
            if(i!=0&&s[i]=='0'&&s[i-1]=='0') continue;
            t.push_back(s[i]);
        }
        swap(t,s);
        int cnt=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1') cnt++;
            else res+=cnt;; 
        }
        return res;
    }
};
