class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int p = 0;
        boolean hasOne = false;
        List<Integer> bits = new ArrayList<>();

        for (char ch : s.toCharArray()) {
            if (ch == '1') {
                bits.add(1);
                hasOne = true;
            } else if (ch == '0' && hasOne) {
                bits.add(0);
                hasOne = false;
            }
        }

        for (int bit : bits) {
            if (bit == 1) {
                p += 1;
            } else {
                ans += p;
            }
        }

        return ans;
    }
}
