class Solution:
    def maxOperations(self, s: str) -> int:
        sm = 0
        n = len(s)
        acc =0
        for i in range(n):
            if s[i] =="1":
                acc +=1
            elif s[i] =="0" and (i>0 and s[i-1] != "0"):
                sm += acc
        return sm
