class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int count1 =0;
        int count0 = 0;

        for(int i=0; i<s.length(); i++){
            if(s[i] == '0'){
                count0++;
            }
            else if(s[i] == '1'){
                if(count0>0){
                    ans = ans + count1;
                    count0 = 0;
                }
                count1++;
            }
        }

        if(count0>0){
            ans = ans + count1;
        }

        return ans;
    }
};
