class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        bool one = false;
        vector<int> oneCnt;

        for (char c : s) {
            if (c == '1') {
                oneCnt.push_back(1);
                one = true;
            } else if (c == '0' && one) {
                oneCnt.push_back(0);
                one = false;
            }
        }

        for (auto x : oneCnt) {
            if (x == 1) cnt += 1;
            else ans += cnt;
        }

        return ans;
    }
};
