class Solution {
public:
    int maxOperations(string s) {

          int n = s.length();
          int count = 0;
          int curr= 0;

          for(int i=n-2;i>=0;i--){
            if(s[i]=='1' && s[i+1]=='0'){
               curr++;
               count+=curr;
            }
            else if(s[i]=='1'){
                count+=curr;
            }
          }

          return count;
    }
};
