class Solution {
public:
    #define ll long long int
    int maxOperations(string s) {
        ll n=s.size();
        ll i=0, f=0, c=0, shi=0;
        while(i<n){
            if(s[i]=='0'){
                if(!f) c+= shi;

                f=1;
            }
            else{
                shi++;

                f=0;
            }

            i++;
        }

        return c;
    }
};
