class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int last = 0;
        bool zer = false;
        for(int i = s.size()-1;i>=0;i--){
            if(s[i] == '0')zer = true;
            if(s[i] == '1'){
                if(zer){
                    last++;
                    cnt+=last;
                }
                else{
                    cnt +=last;
                }
                zer = false;
            }
        }
        return cnt;
    }
};
