class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();

        int onecnt=0;
        int ans=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                if(i&&s[i-1]=='0')ans+=onecnt;
                onecnt++;
            }
        }
        if(s[n-1]=='0')ans+=onecnt;

        return ans;
    }
};
