class Solution {
public:
    int maxOperations(string s) {
        int zeroCnt = 0;
        int ret = 0;
        string ss;
        for(int i = s.size()-1; i >=0 ; i--)
        {
            if (s[i] == '0')
            {
                if(i == s.size() - 1) zeroCnt++;
                else if (s[i+1] != '0') zeroCnt++;
            }
            else if (s[i] == '1') ret += zeroCnt;
            //cout << s[i] << "zeros: " << zeroCnt << " ret: " << ret << endl;
        }
        return ret;
    }
};
