class Solution {
public:
    int maxOperations(string s) {
        int res = 0 , n = s.size() , now = n - 1 , cnt = 0 ;
        bool flag = false;
        for(int i=n-1; ~i; i--) {
            if(s[i] == '1') {
                cnt += flag ;
                res += cnt ;
                flag = false;
            } else {
                flag = true;
            }
        }
        return res;
    }
};
