class Solution:
    def maxOperations(self, s: str) -> int:
        s += "1"
        res = 0
        ones = 0
        for i in range(len(s) - 2):
            if s[i] == "1":
                if s[i + 1] == "0":
                    ones += 1
                    res += ones
                else:
                    ones += 1
        return res
