class Solution {
public:
    int maxOperations(string s) {
        int result = 0,p = 0;
        bool check = 0;
        vector<int> arr;
        for(int i = 0;i<s.length();i++){
            if(s[i] == '1'){
                arr.push_back(1);
                check = true;
            }
            if(s[i] == '0' && check){
                arr.push_back(0);
                check = false;
            }
        }
        for(auto i : arr){
            if(i == 1){
                p++;
            }
            else{
                result+=p;
            }
        }
        return result;
    }
};
