func maxOperations(s string) int {
	tmp := make([]int, 0)
	left := 0
	for left < len(s) && s[left] == '0' {
		left++
	}
	right := left + 1
	for right < len(s) {
		if s[right] == '0' {
			tmp = append(tmp, right-left)
			left = right
			for left < len(s) && s[left] == '0' {
				left++
			}
			right = left + 1
		} else {
			right++
		}
	}
	ans := 0
	for i := 0; i < len(tmp); i++ {
		for j := 0; j <= i; j++ {
			ans += tmp[j]
		}
	}
	fmt.Println(tmp)
	return ans
}
