class Solution {
public:
    int maxOperations(string s) {
        int l=0;//连续的1
        int res=0;
        for(int i=0;i<s.size();++i){
            if(s[i]=='1'){
                if(i>0 && s[i-1]=='0'){
                    res += l;
                }
                l++;
            }
        }
        if(s[s.size()-1]=='0'){
            res += l;
        }
        return res;
    }
};
