class Solution {
public:
    int maxOperations(string s) {
        string ns="";
        int n=s.length();
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                if( (i+1==n) || i+1<n && s[i+1]=='1') ns+=s[i];
            }else{
                ns+=s[i];
            }
        }

        int ns1=ns.size();
        int ans=0,count=0;
        for(int i=0;i<ns1;i++){
            if(ns[i]=='1'){
                count++;
            }else{
                ans+=count;
            }
        }
        return ans;
    }
};
