class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        s=list(s)
        s.append('1')
        ones=[]
        i=0
        while i<len(s):
            if s[i]=='1':
                cnt=0
                while i<len(s) and s[i]=='1':  
                    cnt+=1
                    i+=1
                ones.append(cnt)
            else:
                i+=1
        one=0
        pref=[0]
        for i in range(1,len(ones)):
            pref.append(pref[-1]+ones[i-1])
        # print(pref)
        for i in pref:
            ans+=i
        return ans
