class Solution:
    def maxOperations(self, s: str) -> int:
        if s.count('1') == 0:
            return 0
        count = 0
        ans = 0
        dir = 1
        for i in range(len(s)-1,-1,-1):
            if s[i] == '1' :
                ans += count
                dir = 1
            elif s[i] == '0' and dir == 1:
                count += 1
                dir = -1
        return ans
