class Solution {
public:
    int maxOperations(string s) {
        int leftOne = 0, ans=0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1'){
                leftOne++;
            }else if(i+1==s.length()||s[i+1]=='1'){
                ans+=leftOne;
            }
        }
        return ans;
    }
};
