class Solution:
    def maxOperations(self, s: str) -> int:
        # convert all adjcent 1 into the format of [2,4,1] and then 2 + [6, 1]

        # greedily mover from the left

        digits = collections.deque()

        num_one = 0
        for i in range(len(s)):
            if s[i] == '1':
                num_one += 1
            elif num_one > 0:
                digits.append(num_one)
                num_one = 0

        res = 0
        while(len(digits) > 0):
            adj_one = digits.popleft()
            res += adj_one

            if len(digits) > 0:
                digits[0] += adj_one

        return res
