class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        px = 0
        arr = []
        one = False
        for char in s:
            if char == '1':
                one = True
                arr.append(1)   
            elif char == '0' and one:
                one = False
                arr.append(0)
        for ax in arr:
            if ax == 1:
                px += 1
            else:
                res += px
        return res
