class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0;
        int add = 0;
        for(int i = n - 1; i >= 0; i--){
            if(s[i] == '0'){
                while((i - 1) >= 0 && s[i - 1] == '0'){
                      i--;
                }    
                add++;
            }
            else
                cnt += add;
            //cout << cnt << " " << add << "\n";
        }
        return cnt;
    }
};
