class Solution {
public:
    int maxOperations(string s) {
        s+='1';
        int cnt=0,ans=0, f=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                if(f==0)cnt++;
                else{
                    ans+=cnt;
                    cnt++;f=0;
                }
            }
            if(s[i]=='0' and cnt!=0)f=1;
        }
        return ans;
    }
};
