class Solution {
public:
    int maxOperations(string s) {
        //后面有几段连续1 都做出贡献
        int n = s.size();
        s = " " + s;
        s.push_back('1');

        int cnt = 0;
        std::vector<int> sum(n + 2, 0);
        for(int i = n + 1; i >= 1; i--) {
            int idx = i;
            while(idx >= 0 && s[idx] == '1' && s[idx] == s[i]) idx--;
            if(s[i] == '1') {
                i = idx + 1;
                sum[idx] = 1;
                cnt++;
                //std::cout << idx << '\n';
            }
        }
        for(int i = n - 1; i >= 0; i--) sum[i] += sum[i + 1];

        int res = 0;
        for(int i = 0; i <= n; i++) {    
            if(s[i] == '1') res += sum[i];
        }
        return res;
    }
};
