class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0;
        int temp = 0;
        int i = n-1;
        while(i>=0){
            if(s[i]=='1'){
                ans += temp;
                i--;
            }
            else{
                int x = s[i];
                while(i>=0 && s[i]==x){
                    i--;
                }
                temp++;
            }
        }
        return ans;
    }
};
