impl Solution {
    pub fn max_operations(s: String) -> i32 {
        let mut res = 0;
        let mut prefix_cnt = 0;
        let arr_s: Vec<char> = s.chars().collect::<Vec<_>>();
        let n = arr_s.len();
        for i in 0..n {
            let c = arr_s[i];
            if c == '1' && i != n - 1 && arr_s[i + 1] == '0' {
                prefix_cnt += 1;
                res += prefix_cnt;
            } else if c == '1' {
                prefix_cnt += 1;
            }
        }
        return res;
    }
}
