class Solution {
public:
   int maxOperations(const string& binaryStr) {
    int totalOperations = 0;
    int countOnes = 0;
    bool encounteredOne = false;
    vector<int> significantBits;

    for (char ch : binaryStr) {
        if (ch == '1') {
            significantBits.push_back(1);
            encounteredOne = true;
        } else if (ch == '0' && encounteredOne) {
            significantBits.push_back(0);
            encounteredOne = false;
        }
    }

    for (int bit : significantBits) {
        if (bit == 1) {
            countOnes++;
        } else {
            totalOperations += countOnes;
        }
    }

    return totalOperations;
}
};
