class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int one=0;
        for(int i=0;i<s.size()-1;i++){
            if(s[i]=='1'){
                one++;
            if(s[i+1]=='0')ans+=one;
            }
            // cout<<one<<ans<<" ";
        }
        return ans;

    }
};
