class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, x = 0;
        bool flag = true;
        int i = 0;
        while (i < s.size()) {
            if (s[i] == '1') {
                x++;
                flag = true;
            } else {
                if (flag) ans += x;
                flag = false;
            }
            i++;
        }
        return ans;
    }
};
