class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        bool countiZero = false;

        for(int i = 0 ; i < s.length()-1 ; i++){
            if (s[i] == '1'){
                cnt++;
                countiZero = false;
            }
            else{
                if(!countiZero) {
                    ans = ans + cnt;
                    countiZero = true;
                }
            }
        }
        if(s[s.length()-1] == '0' && !countiZero) ans = ans + cnt;
        return ans;
    }
};
