class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt1 = 0;
        int n = s.length();
        int i = 0;
        while(i<n)  {
            while(i<n && s[i]=='1') {
                cnt1++;
                i++;
            }
            while(i<n && s[i]=='0') {
                i++;
            }
            if(i<n || (i==n && s[n-1]=='0'))
                ans += cnt1;
        }
        return ans;
    }
};
