class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(),ans = 0,cnt = 0;
        if(s[n-1] == '0')
        cnt++;
        for(int i=n-1;i>=0;i--){
            if(i < n-1 && s[i] == '0' && s[i+1] == '1')
            cnt++;
            if(s[i] == '1')
            ans += cnt;
        }
        return ans;
    }
};
