class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        stack<char> st;
        for(int i=0;i<n;){
            if(s[i] == '0'){
                ans += st.size();
                int j = i;
                while(j<n && s[j] == '0') j++;
                i=j;
            }
            else{
                st.push(s[i]);
                i++;
            }
        }
        return ans;
    }
};
