class Solution(object):
    def maxOperations(self, s):
        result = 0
        i = 0
        flag_one = 0
        bits = []

        for j in s:
            if j=='1':
                bits.append(1)
                flag_one = 1
            elif j == '0' and flag_one:
                bits.append(0)
                flag_one = 0

        for j in bits:
            if j==1:
                i+=1
            else:
                result+=i
        return result
